hudson.tasks.Shell.command:
#!/bin/bash set -e source /opt/vappio-scripts/clovrEnv.sh
DATE=`date +"%m-%d-%Y-%T" | sed -e 's/:/_/g'` DATA_DIR=`mktemp -p /mnt/user_data -d`
echo "Downloading data..." wget -O ${DATA_DIR}/InfantGut16S.tar.gz http://cb2.igs.umaryland.edu/clovr/Public_Benchmarks/CloVR-16S/InfantGutMicrobiome/InfantGut16S.tar.gz
echo "Extracting data..." tar xvf ${DATA_DIR}/InfantGut16S.tar.gz -C ${DATA_DIR} chmod -R 777 ${DATA_DIR}
vp-add-dataset -o --tag-name=clovr_16S_chimera_input_local_${DATE} ${DATA_DIR}/InfantGut16S.fasta vp-add-dataset -o --tag-name=clovr_16S_chimera_mapping ${DATA_DIR}/InfantGut16S.map
vp-describe-protocols --config-from-protocol=clovr_16S \ -c input.FASTA_TAG=clovr_16S_chimera_input_local_${DATE} \ -c input.MAPPING_TAG=clovr_16S_chimera_mapping \ -c cluster.CLUSTER_NAME='local' \ -c cluster.CLUSTER_CREDENTIAL='local' \ -c cluster.TERMINATE_ONFINISH=false \ -c pipeline.PIPELINE_DESC="Hudson CloVR 16S Chimera Test local ${DATE}" \ > /tmp/$$.pipeline.conf.${DATE}
TASK_NAME=`vp-run-pipeline --print-task-name --pipeline-config /tmp/$$.pipeline.conf.${DATE} --overwrite`
if [ "$?" == "1" ]; then echo "vp-run-pipeline failed to run" rm -rf ${DATA_DIR} exit 1 fi
vp-describe-task --name local --exit-code --block $TASK_NAME rm -rf ${DATA_DIR}
exit $?
