hudson.tasks.Shell.command:
#!/bin/bash set -e source /opt/vappio-scripts/clovrEnv.sh
DATE=`date +"%m-%d-%Y-%T" | sed -e 's/:/_/g'` DATA_DIR=`mktemp -p /mnt/user_data -d`
echo "Downloading data..." wget --quiet -O ${DATA_DIR}/InfantGutMetagenome.tar.gz http://cb2.igs.umaryland.edu/clovr/Public_Benchmarks/CloVR-Metagenomics/InfantGutMetagenome/InfantGutMetagenome.tar.gz
echo "Extracting data..." tar xfv ${DATA_DIR}/InfantGutMetagenome.tar.gz -C ${DATA_DIR} chmod -R 777 ${DATA_DIR}
vp-add-dataset -o --tag-name=clovr_metagenomics_noorfs_fasta_local_${DATE} ${DATA_DIR}/Age100d.fasta ${DATA_DIR}/Age118d.fasta ${DATA_DIR}/Age371d.fasta ${DATA_DIR}/Age3d.fasta ${DATA_DIR}/Age413d.fasta ${DATA_DIR}/Age432d.fasta ${DATA_DIR}/Age441d.fasta ${DATA_DIR}/Age454d.fasta ${DATA_DIR}/Age6d.fasta ${DATA_DIR}/Age85d.fasta ${DATA_DIR}/Age92d.fasta ${DATA_DIR}/Age98d.fasta vp-add-dataset -o --tag-name=clovr_metagenomics_noorfs_map ${DATA_DIR}/InfantGutMetagenome.map
vp-describe-protocols --config-from-protocol=clovr_metagenomics_noorfs \ -c input.FASTA_TAG=clovr_metagenomics_noorfs_fasta_local_${DATE} \ -c input.MAPPING_TAG=clovr_metagenomics_noorfs_map \ -c cluster.CLUSTER_NAME='local' \ -c cluster.CLUSTER_CREDENTIAL='local' \ -c cluster.TERMINATE_ONFINISH=false \ -c pipeline.PIPELINE_DESC="Hudson CloVR Metagenomics Noorfs Test local ${DATE}" \ > /tmp/$$.pipeline.conf.${DATE}
TASK_NAME=`vp-run-pipeline --print-task-name --pipeline-config /tmp/$$.pipeline.conf.${DATE} --overwrite`
if [ "$?" == "1" ]; then echo "vp-run-pipeline failed to run" rm -rf ${DATA_DIR} exit 1 fi
vp-describe-task --name local --exit-code --block $TASK_NAME rm -rf ${DATA_DIR}
exit $?
